import { useEffect, useRef, useState } from "react";

export default function Navbar(){
    let navtext = "";
    var clicked = false;

    const inputRef = useRef(0)
    let [x_html,setX_html_width] = useState();
    useEffect(() => {
        setX_html_width(window.screen.availWidth)
    })
    if (x_html >= 590){
        navtext = "flex space-x-5"
    }
    else{
        navtext = "inline-grid space-x-5"
    }

    const showlist = () =>{
        if (clicked ===false){
            navtext = "inline-grid space-x-5"
            clicked = true
        }else{
            navtext = "flex space-x-5"
        }
    }
    return(

        <nav className="w-full bg-black">
        <ul className={navtext} ref={inputRef}>
        <button onClick={showlist} id="minmax" className="pl-5 left-0 text-left" >|||</button>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/myprojects">My Projects</a></li>
          <li><a href="/blogs">Blogs</a></li>
        </ul>
      </nav>
        
    );
}
