import { useEffect, useRef, useState } from "react";

async function changeVisible(){
  var options = document.getElementById("a")
  options.style.visibility = "hidden"
  var options2 = document.getElementById("b")
  options2.style.visibility = "hidden"
  var options3 = document.getElementById("c")
  options3.style.visibility = "hidden"
  var options4 = document.getElementById("d")
  options4.style.visibility = "hidden"
  
}

export default function Navbar(){
  let navtext = "";
  
    var inputRef = useRef(0);
    let [x_screen,setX_screen] = useState(0) ;
    useEffect(() => {
      setX_screen(window.screen.availWidth);
    },[])
    
    if(x_screen <= 600) {
      navtext = "inline-grid space-x-5"
      changeVisible()
      
      
      

    }else{
      navtext = "flex space-x-5"
      document.getElementById("minmax").style.visibility = "hidden"
      
    }
    return(

        <nav className="w-full bg-black">
        <ul className={navtext} ref={inputRef} >
        <button id="minmax" className="pl-5 left-0 text-left" >|||</button>
          <li><a href="/" id="a">Home</a></li>
          <li><a href="/about" id="b">About</a></li>
          <li><a href="/myprojects" id="c">My Projects</a></li>
          <li><a href="/blogs" id="d">Blogs</a></li>
        </ul>
      </nav>
        
    );
}
