import { useEffect, useRef, useState } from "react";
var clicked = false;
export default function Navbar(){
    let [navtext,setNavtext] = useState();
    

    const inputRef = useRef(0)
    let [x_html,setX_html_width] = useState();
    useEffect(() => {
        setX_html_width(window.screen.availWidth)
    })
    if (x_html >= 590){
        setNavtext("flex space-x-5")
    }
    else{
        setNavtext("inline-grid space-x-5")
    }

    const showlist = () =>{
        console.log(clicked)
        if (clicked ===false){
            setNavtext("inline-grid space-x-5")
            clicked = true
        }else{
            setNavtext("flex space-x-5")
            clicked = false
        }
    }
    return(

        <nav className="w-full bg-black" ref={inputRef}>
        <ul className={navtext} >
        <button onClick={showlist} id="minmax" className="pl-5 left-0 text-left" >|||</button>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/myprojects">My Projects</a></li>
          <li><a href="/blogs">Blogs</a></li>
        </ul>
      </nav>
        
    );
}
