import { useEffect, useRef, useState } from "react";

var navtext = "flex space-x-5"
var clicked = false;
async function makeVisible(){
  var list = document.querySelectorAll(".list")
  list.forEach(listItem => {
    listItem.style.visibility = "visible"
  })
}

async function makeInvisible(){
  var list = document.querySelectorAll(".list")
  list.forEach(listItem => {
    listItem.style.visibility = "hidden"
  })
}

async function dropdown(){
  if(clicked === false){
    makeVisible()
    // setnavtext("inline-grid space-x-5")
    navtext = "inline-grid space-x-5"
    console.log(navtext)
    clicked = true
    console.log(document.querySelector("nav"))
  }
  else{
    // setnavtext("flex space-x-5")
    navtext = "flex space-x-5"
    makeInvisible()
    
    clicked = false
    
    console.log(navtext)
    }
}

export default function Navbar(){
    
  
    var inputRef = useRef(0);
    let [x_screen,setX_screen] = useState(0) ;
    // let [navtext,setnavtext] = useState("flex space-x-5");
    useEffect(() => {
      setX_screen(window.screen.availWidth);
    },[])
    
    if(x_screen <= 600) {
      makeInvisible()
    }else{
      makeVisible()
      
    }
    
    
      
    return(

        <nav className="w-full bg-black">
        <ul className={navtext} ref={inputRef} id="nav">
        <button id="minmax" onClick={dropdown} className="pl-5 left-0 text-left" >|||</button>
          <li><a href="/" id="a" className="list">Home</a></li>
          <li><a href="/about" id="b" className="list">About</a></li>
          <li><a href="/myprojects" id="c" className="list">My Projects</a></li>
          <li><a href="/blogs" id="d" className="list">Blogs</a></li>
        </ul>
      </nav>
        
    );
}
