import {Link} from "react-router-dom"
import { useRef,useState,useEffect } from "react"
// const icon = require("./icons/channel.jpg")

export default function Navbar(){
    const inputRef = useRef(0);
    let [x_html,setX_html] = useState(0) ;
    let [x_html_width,setX_html_width] = useState(0);
    let [x_screen,setX_screen] = useState(0) ;
    useEffect(() => {
        setX_html(inputRef.current.getBoundingClientRect().height)
        setX_screen(window.screen.a)
        setX_html_width(inputRef.getBoundingClientRect().width)
    }, [])
    if(x_html_width > x_screen)
    return(

        
        

        <div className="scrollbar scrollbar-thumb-cyan-800 scrollbar-track-sky-500 overflow-x-scroll sticky top-0" >
        <nav className='navbar text-purple-400 font-high bg-black '>
            <ul>
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/">Home</Link>
                </li >
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/about">About</Link>
                </li>
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/myprojects">My Projects</Link>
                </li>
                
            </ul>
        </nav>
        </div>
        
    );
}
