import { useState } from "react";
import {Link} from "react-router-dom"



export default function Navbar(){
    var x = false;

    if (x === false){
        document.getElementById("container").style.display ="inline-grid"
        document.getElementById("nav").style.width = Screen.availWidth
        document.getElementById("container").style.paddingLeft = 0;
        const navItem = document.querySelectorAll(".navItem")
        navItem.forEach((navItem) => {
            navItem.style.paddingLeft = 0;
            navItem.style.opacity = "1"
            navItem.style.pointerEvents = ""
        })
        
    
        
        x = true;
    }
    else{
        document.getElementById("container").style.display = "flex"
        document.getElementById("container").style.paddingLeft = "20px";
        const navItem = document.querySelectorAll(".navItem")
        navItem.forEach((navItem) => {

            navItem.style.opacity = "0"
            navItem.style.pointerEvents = "none"
        })
        
        x = false;
    }
    
    return(
        <div className= "scrollbar-thin scrollbar-thumb-cyan-800 scrollbar-track-transparent overflow-x-scroll sticky top-0 bg-black w-full">
        <nav className="navbar text-purple-400 overflow-hidden flex sticky top-0 z-10 font-high shadow-2xl shadow-black ">
            <ul className="px-5 flex space-x-5">
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/">Home</Link>
                </li>
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/about">About Me</Link>
                </li>
                <li>
                    <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/myprojects">My Projects</Link>
                </li>
                <li>
                <Link className="mx-4 text-center px-4 py-4 rounded-xl hover:bg-emerald-400 duration-300 hover:text-black flex" to="/blogs">Blogs</Link>
                </li>
                
            </ul>
        </nav>
        </div>
    );
}
