import { useEffect, useRef, useState } from "react";

async function changeVisible(){
  document.getElementById("a").classList.add("invisible")
  
}
var clicked = false;


export default function Navbar(){
    
  
    var inputRef = useRef(0);
    let [x_screen,setX_screen] = useState(0) ;
    let [navtext,setnavtext] = useState("flex space-x-5");
    useEffect(() => {
      setX_screen(window.screen.availWidth);
    },[])
    
    if(x_screen <= 600) {
      changeVisible()
    }else{
      navtext = "flex space-x-5"
      document
    }
    var options = document.getElementById("a")
    var dropdown = () => {
      
      if(clicked === false){
        setnavtext("inline-grid space-x-5")
        options.classList.remove("hidden")
        options.classList.add("visible")

        
        console.log(options)
        console.log(navtext)
      }
      else{
        setnavtext("flex space-x-5")
        changeVisible()
        
        clicked = false
        console.log(options)
        console.log(navtext)
        }
        
      }

      
    return(

        <nav className="w-full bg-black">
        <ul className={navtext} ref={inputRef} >
        <button id="minmax" onClick={dropdown} className="pl-5 left-0 text-left" >|||</button>
          <li><a href="/" id="a">Home</a></li>
          <li><a href="/about" id="b">About</a></li>
          <li><a href="/myprojects" id="c">My Projects</a></li>
          <li><a href="/blogs" id="d">Blogs</a></li>
        </ul>
      </nav>
        
    );
}
